source.#include "Litter Globals.r"	//	// -----------------------------------------	//	// Other resource definitions 	//#define LPAssistIn1			"Bang (or float to set new seed)"#define LPAssistOut1		"Float (1/f chaos)"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistIn1		lpStrIndexLastStandard + 2,		LPAssistOut1		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray */			LPStdStrings,					// Standard Litter Strings						// Assist strings			LPAssistIn1,			LPAssistOut1		}	};#endif