/*	File:		julia~.r	Contains:	Resources for Max external object julia~.	Written by:	Peter Castine	Copyright:	© 2001-2002 Peter Castine. All rights reserved.	Change History (most recent first):         <4>    10Ð12Ð04    pc      Update to use proxy_getinlet()         <3>      9Ð1Ð04    pc      Make vers information carbon/classic-savvy. Drop faux 'Vers'                                    resource.         <2>  29Ð11Ð2002    pc      Tidy up initial check in.         <1>  29Ð11Ð2002    pc      Initial check in.*/	//	// Configuration values for this object	//	// These must be unique within Litter Package. The Names must match constant values used	// in the C/C++ source code (we try never to access resources by ID).#define LPobjID			17532#define LPobjName		"lp.julie~"	// Values used in the 'vers'(1) resource#define LPobjStarter		1								// Comment out for Pro Bundles#ifdef __LP_CARBON_VERSION__	#define LPobjMajorRev		0x01	#define LPobjMinorRev		0x00	#define LPobjStage			development	#define LPobjStageBuild		0x03	#define LPobjRegion			0								// US	#define LPobjShortStr		"1.0d3 (OS X)"	#define LPobjLongStr		"1.0d3 (OS X), Copyright © 2001-2003 Peter Castine."#else	#define LPobjMajorRev		0x01	#define LPobjMinorRev		0x00	#define LPobjStage			development	#define LPobjStageBuild		0x03	#define LPobjRegion			0								// US	#define LPobjShortStr		"1.0d3 (Classic OS)"	#define LPobjLongStr		"1.0d3 (Classic OS), Copyright © 2001-2003 Peter Castine."#endif	// The following sets up the 'mAxL' and 'vers' resources	// It relies on the values above for resource IDs and names, as	// well as concrete values for the 'vers'(1) resource.#include "Litter Globals.r"	//	// Resource definitions 	//resource 'STR#' (LPobjID, LPobjName) {	{	/* array StringArray */				// Copies of 'vers' strings		LPobjShortStr, LPobjLongStr, LPShortStr, LPLongStr,				// Assist strings		/* Inlets */		"Float/Signal (z0.real)",		"Float/Signal (z0.imaginary)",		"Float/Signal (c.real)",		"Float/Signal (c.imaginary)",				/* Outlets */		"Signal (Real component)",		"Signal (Imaginary component)"	}};