/*	File:		vera.r	Contains:	Resources for vera object (Verhulst Algorithm).	Written by:	Peter Castine	Copyright:	© 2002 Peter Castine. All rights reserved.	Change History (most recent first):         <1>  30Ð12Ð2002    pc      Initial Check in.Drop faux 'Vers' resource and related                                    modifications. Add object version to DoInfo().*/	//	// Configuration values for this object	//	// These must be unique within Litter Package. The Names must match constant values used	// in the C/C++ source code (we try never to access resources by ID).#define LPobjID			17551#define LPobjName		"lp.vera"	// Values used in the 'vers'(1) resource//#define LPobjStarter		1								// Comment out for Pro Bundles#ifdef __LP_CARBON_VERSION__	#define LPobjMajorRev		0x01	#define LPobjMinorRev		0x00	#define LPobjStage			development	#define LPobjStageBuild		0x02	#define LPobjRegion			0								// US	#define LPobjShortStr		"1.0d2 (OS X)"	#define LPobjLongStr		"1.0d2 (OS X), Copyright © 2002-2003 Peter Castine."#else	#define LPobjMajorRev		0x01	#define LPobjMinorRev		0x00	#define LPobjStage			development	#define LPobjStageBuild		0x02	#define LPobjRegion			0								// US	#define LPobjShortStr		"1.0d2 (Classic OS)"	#define LPobjLongStr		"1.0d2 (Classic OS), Copyright © 2002-2003 Peter Castine."#endif	// The following sets up the 'mAxL' and 'vers' resources	// It relies on the values above for resource IDs and names, as	// well as concrete values for the 'vers'(1) resource.#include "Litter Globals.r"	//	// Resource definitions 	//resource 'STR#' (LPobjID, LPobjName) {	{	/* array StringArray */				// Copies of 'vers' strings		LPobjShortStr, LPobjLongStr, LPShortStr, LPLongStr,				// Assist strings		/* Inlets */		"Bang (Generate next point), Float/List (Growth rates), Symbol (Function)",		"Float (Seed population)",				/* Outlets */		"Float (Next population)"	}};