		// 1-255 (0 for Golden Master only)#define LPobjRegion			0							// US#define LPobjVersStr		"2.1fc1"#define LPobjCRYears		"2001-08"#define	LPobjLitterCategory	"Litter RNGs"				// Litter Category#define LPobjMax3Category	"Arith/Logic/Bitwise"		// Category for Max 2.2 - 3.6x#define LPobjMax4Category	"Math"						// Category starting at Max 4	// Description string (for Windows Properties box, taken from documentation)#define LPobjDescription	"Generate random numbers from a Cauchy distribution"	// The following sets up the 'mAxL' and 'vers' resources on Mac OS	// and VERSIONINFO resource on Windows	// It relies on the values above for resource IDs and names, as	// well as concrete values for the 'vers'/VERSIONINFO resources.#include "Litter Globals.r"	//	// -----------------------------------------	//	// Other resource definitions 	//	// Assistance strings	#define LPAssistIn1			"Bang (Generate random number)"#define LPAssistIn2			"Float (tau)"#define LPAssistIn3			"Float (location)"#define LPAssistOut1		"Float (Value from %s Cauchy distribution)"#define LPOutFragNeg		"negative"#define LPOutFragSym		"symmetrical"#define LPOutFragPos		"positive"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistIn1		lpStrIndexLastStandard + 2,		LPAssistIn2		lpStrIndexLastStandard + 3,		LPAssistIn3		lpStrIndexLastStandard + 4,		LPAssistOut1		lpStrIndexLastStandard + 5,		LPOutFragNeg		lpStrIndexLastStandard + 6,		LPOutFragSym		lpStrIndexLastStandard + 7,		LPOutFragPos		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray */			LPStdStrings,					// Standard Litter Strings						// Assist strings			LPAssistIn1, LPAssistIn2, LPAssistIn3, LPAssistOut1,						// Fragments used by LPAssistOut1			LPOutFragNeg, LPOutFragSym, LPOutFragPos		}	};#endif