rs		"2001-08"#define	LPobjLitterCategory	"Litter RNGs"				// Litter Category#define LPobjMax3Category	"Arith/Logic/Bitwise"		// Category for Max 2.2 - 3.6x#define LPobjMax4Category	"Math"						// Category starting at Max 4	// Description string (for Windows Properties box, taken from documentation)#define LPobjDescription	"Generate random numbers from linear and triangular distributions"	// The following sets up the 'mAxL' and 'vers' resources on Mac OS	// and VERSIONINFO resource on Windows	// It relies on the values above for resource IDs and names, as	// well as concrete values for the 'vers'/VERSIONINFO resources.#include "Litter Globals.r"	//	// -----------------------------------------	//	// Other resource definitions 	//	// Assistance strings	#define LPAssistIn1			"Bang, also float (mapping) and symmetry messages"#define LPAssistOut1		"Float (Random value)"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistIn1		lpStrIndexLastStandard + 2,		LPAssistOut1		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray */			LPStdStrings,					// Standard Litter Strings						// Assist strings			LPAssistIn1,			LPAssistOut1		}	};#endif