scription string (for Windows Properties box, taken from documentation)#define LPobjDescription	"Parametric linear congruence method"	// The following sets up the 'mAxL' and 'vers' resources	// It relies on the values above for resource IDs and names, as	// well as concrete values for the 'vers'(1) resource.#include "Litter Globals.r"	//	// -----------------------------------------	//	// Other resource definitions 	//	// Assistance strings#define LPAssistIn1			"Bang (or Int to seed)"#define LPAssistIn2			"Int (multiplier)"#define LPAssistIn3			"Int (adder)"#define LPAssistIn4			"Int (modulo)"#define LPAssistOut1		"Int (more-or-less random value)"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistIn1		lpStrIndexLastStandard + 2,		LPAssistIn2		lpStrIndexLastStandard + 3,		LPAssistIn3		lpStrIndexLastStandard + 4,		LPAssistIn4		lpStrIndexLastStandard + 5,		LPAssistOut1		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray: 4 elements */			LPStdStrings,								// Standard Litter Strings						// Assist strings			LPAssistIn1, LPAssistIn2,			 		// Inlets			LPAssistIn3, LPAssistIn4,			LPAssistOut1								// Outlets		}	};#endif