ine LPAssistIn2			"Int (NN factor)"#define LPAssistOut1		"Gaussian noise"#define LPAssistOut2		"Dump"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistIn1		lpStrIndexLastStandard + 2,		LPAssistOut1		lpStrIndexLastStandard + 3,		LPAssistOut2		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray */			LPStdStrings,								// Standard Litter Strings						// Assist strings			LPAssistIn1,								// Inlets			LPAssistOut1, LPAssistOut2					// Outlets		}	};#endif		