1			"Jitter matrix to analyze"#define LPAssistOut1		"List (minima, 1/plane)"#define LPAssistOut2		"List (maxima, 1/plane)"#define LPAssistOut3		"List (mean values, 1/plane)"#define LPAssistOut4		"List (std. dev. values, 1/plane)"#define LPAssistOut5		"List (skew values, 1/plane)"#define LPAssistOut6		"List (kurtosis values, 1/plane)"#define LPAssistOut7		"Dump"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistIn1		lpStrIndexLastStandard + 2,		LPAssistOut1		lpStrIndexLastStandard + 3,		LPAssistOut2		lpStrIndexLastStandard + 4,		LPAssistOut3		lpStrIndexLastStandard + 5,		LPAssistOut4		lpStrIndexLastStandard + 6,		LPAssistOut5		lpStrIndexLastStandard + 7,		LPAssistOut6		lpStrIndexLastStandard + 8,		LPAssistOut7		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray */			LPStdStrings,					// Standard Litter Strings						// Assist strings			LPAssistIn1,			LPAssistOut1,			LPAssistOut2,			LPAssistOut3,			LPAssistOut4,			LPAssistOut5,			LPAssistOut6,			LPAssistOut7		}	};#endif