/*    File:       flim.r    Contains:   Resources for external object vim.    Written by: Peter Castine.    Copyright:  © 2000-2002 Peter Castine. All rights reserved.    Change History (most recent first):         <9>     9Ð12Ð04    pc      Minor updates         <8>     21Ð1Ð04    pc      Go to final status.         <7>     10Ð1Ð04    pc      Modified for Rez/RC Compiler cross-platform compatibility.         <6>    7Ð7Ð2003    pc      Bump version, use STR# resource for Object List categories.         <5>   22Ð3Ð2003    pc      Bump version to final candidate.         <4>  30Ð12Ð2002    pc      Use 'STR#' resource instead of faux 'Vers' resource for storing                                    version information used at run-time.         <3>  29Ð12Ð2002    pc      Added for to allow identification of OS X-savvy externals via                                    their 'vers' resources.         <2>  29Ð11Ð2002    pc      Tidy up initial check in.         <1>  29Ð11Ð2002    pc      Initial Check-in.*/	// -----------------------------------------	// Configuration values for this object	//	// The following two must be unique within Litter Package. 	// The Names must match constant values used in the C/C++ source code	// (we try never to access resources by ID).	//#define LPobjID			17507#define LPobjName		"lp.vim"	// 'vers' stuff we need to maintain manually//#define LPobjStarter		1							// Comment out for Pro Bundles#define LPobjMajorRev		1#define LPobjMinorRev		2							// NEW: separate Minor revision and#define LPobjBugFix			0							//		bug fix data#define LPobjStage			finalStage#define LPobjStageBuild		1#define LPobjRegion			0							// US#define LPobjVersStr		"1.2fc1"#define LPobjCRYears		"2001-08"#define	LPobjLitterCategory	"Litter Mutators"#define LPobjMax3Category	"Control"					// Category for Max 2.2 - 3.6x#define LPobjMax4Category	"Control"					// Category starting at Max 4	// Description string (for Windows Properties box, taken from documentation)#define LPobjDescription	"Interval mutation of numeric values"	// The following sets up the 'mAxL' and 'vers' resources on Mac OS	// and VERSIONINFO resource on Windows	// It relies on the values above for resource IDs and names, as	// well as concrete values for the 'vers'/VERSIONINFO resources.#include "Litter Globals.r"	//	// -----------------------------------------	//	// Other resource definitions 	//	// Assistance strings	#ifdef RC_INVOKED 			// Special characters for Windows		#define		ellipsisSymbol		"..."		#define		muSymbol			0xb5		#define		piSymbol			"p"		#define		deltaSymbol			"d"	#else						// Special characters for Mac OS		#define		ellipsisSymbol		"É"		#define		muSymbol			"µ"		#define		piSymbol			"¹"		#define		deltaSymbol			"¶"	#endif	#define LPAssistIn1			"Float or Int (Source). Lots of other messages."#define LPAssistIn2			"Float or Int (Target)"#define LPAssistIn3			"Float in [0.0" ellipsisSymbol "1.0] (Mutation Index, " muSymbol ")"#define LPAssistIn4			"Float in [0.0" ellipsisSymbol "1.0) (Clumping Factor, " piSymbol ")"#define LPAssistIn5			"Float in [-1.0" ellipsisSymbol "1.0] (Delta Emphasis, " deltaSymbol ")"#define LPAssistOut1		"Float (Mutant)"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistIn1		lpStrIndexLastStandard + 2,		LPAssistIn2		lpStrIndexLastStandard + 3,		LPAssistIn3		lpStrIndexLastStandard + 4,		LPAssistIn4		lpStrIndexLastStandard + 5,		LPAssistIn5		lpStrIndexLastStandard + 6,		LPAssistOut1		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray */			LPStdStrings,					// Standard Litter Strings						// Assist strings			LPAssistIn1,			LPAssistIn2,			LPAssistIn3,			LPAssistIn4,			LPAssistIn5,			LPAssistOut1		}	};#endif