ation)#define LPobjDescription	"Frequency-domain mutation of signals"	// The following sets up the 'mAxL', 'vers', and 'Vers' resources	// It relies on the values above for resource IDs and names, as	// well as concrete values for the 'vers'(1) resource.#include "Litter Globals.r"	//	// -----------------------------------------	//	// Other resource definitions 	//	// Assistance strings	#ifdef RC_INVOKED 			// Special characters for Windows		#define		ellipsisSymbol		"..."		#define		muSymbol			0xb5		#define		piSymbol			"p"		#define		deltaSymbol			"d"	#else						// Special characters for Mac OS		#define		ellipsisSymbol		"É"		#define		muSymbol			"µ"		#define		piSymbol			"¹"		#define		deltaSymbol			"¶"	#endif	#define LPAssistIn1			"Signal (Source 1). Lots of other messages."#define LPAssistIn2			"Signal (Source 2)."#define LPAssistIn3			"Signal (Target 1)"#define LPAssistIn4			"Signal (Target 2)"#define LPAssistIn5			"Signal or Float in [0.0" ellipsisSymbol "1.0] (Mutation Index, " muSymbol ")"#define LPAssistIn6			"Signal or Float in [0.0" ellipsisSymbol "1.0) (Clumping Factor, " piSymbol ")"#define LPAssistIn7			"Signal or Float in [-1.0" ellipsisSymbol "1.0] (Delta Emphasis, " deltaSymbol ")"#define LPAssistOut1		"Signal (Mutant 1)"#define LPAssistOut2		"Signal (Mutant 2)"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistIn1		lpStrIndexLastStandard + 2,		LPAssistIn2		lpStrIndexLastStandard + 3,		LPAssistIn3		lpStrIndexLastStandard + 4,		LPAssistIn4		lpStrIndexLastStandard + 5,		LPAssistIn5		lpStrIndexLastStandard + 6,		LPAssistIn6		lpStrIndexLastStandard + 7,		LPAssistIn7		lpStrIndexLastStandard + 8,		LPAssistOut1		lpStrIndexLastStandard + 9,		LPAssistOut2		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray */			LPStdStrings,					// Standard Litter Strings						// Assist strin