/*	File:		epoisse~.r	Contains:	Resources for Max/MSP external object creating "pitched noise".	Written by:	Peter Castine	Copyright:	 © 2000-2002 Peter Castine. All rights reserved.	Change History (most recent first):         <5>   26Ð4Ð2006    pc      Strange code duplication crept in. Fixed.         <4>   23Ð3Ð2006    pc      Update version.         <3>    14Ð10Ð04    pc      Update to new object name         <2>     11Ð4Ð04    pc      Update version         <1>     18Ð3Ð04    pc      first checked in.*/	//	// Configuration values for this object	//	// These must be unique within Litter Package. The Names must match constant values used	// in the C/C++ source code (we try never to access resources by ID).#define LPobjID			17490#define LPobjName		"lp.epoisse~"	// -----------------------------------------	// 'vers' stuff we need to maintain manually#define LPobjStarter		1							// Comment out for Pro Bundles#define LPobjMajorRev		1							// 1-99	(decimal)#define LPobjMinorRev		0							// 0-9	(decimal)#define LPobjBugFix			1							// 0-9	(deicmal)#define LPobjStage			alphaStage					// Standard Apple stage#define LPobjStageBuild		2							// 1-255 (0 for Golden Master only)#define LPobjRegion			0							// US#define LPobjVersStr		"1.0.1a2"#define LPobjCRYears		"2001-09"#define	LPobjLitterCategory	"Litter Signals"			// Litter Category#define LPobjMax3Category	"MSP"						// Category for Max 2.2 - 3.6x#define LPobjMax4Category	"MSP Synthesis"				// Category starting at Max 4	// Description string (for Windows Properties box, taken from documentation)#define LPobjDescription	"Pitched noise"	// The following sets up the 'mAxL' and 'vers' resources	// It relies on the values above for resource IDs and names, as	// well as concrete values for the 'vers'(1) resource.#include "Litter Globals.r"	//	// -----------------------------------------	//	// Other resource definitions 	//	// Assistance strings#define LPAssistIn1			"Float (frequency)"#define LPAssistIn2			"Float (Pi: pitchiness factor)"#define LPAssistIn3			"Float (Rho: Hurst factor, the color of noise)"#define LPAssistOut1		"Signal (Pitched noise)"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistIn1		lpStrIndexLastStandard + 2,		LPAssistIn2		lpStrIndexLastStandard + 3,		LPAssistIn3		lpStrIndexLastStandard + 4,		LPAssistOut1		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray */			LPStdStrings,						// Standard Litter Strings						// Assist strings			LPAssistIn1, LPAssistIn2, LPAssistIn3,	// Inlets			LPAssistOut1							// Outlets		}	};#endif