6x#define LPobjMax4Category	"MSP Synthesis"				// Category starting at Max 4	// Description string (for Windows Properties box, taken from documentation)#define LPobjDescription	"Gaussian noise"	// The following sets up the 'mAxL' and 'vers' resources	// It relies on the values above for resource IDs and names, as	// well as concrete values for the 'vers'(1) resource.#include "Litter Globals.r"	//	// -----------------------------------------	//	// Other resource definitions 	//		#ifdef RC_INVOKED		#define LPmuString	"mu"	#else		#define LPmuString	"µ"	#endif	// Assistance strings#define LPAssistIn1			"Signal/float (" LPmuString ": DC Offset)"#define LPAssistIn2			"Signal/float (s: Scaling factor)"#define LPAssistOut1		"Signal (Gaussian noise)"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistIn1		lpStrIndexLastStandard + 2,		LPAssistIn2		lpStrIndexLastStandard + 3,		LPAssistOut1		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray: 4 elements */			LPStdStrings,						// Standard Litter Strings						// Assist strings			LPAssistIn1, LPAssistIn2,			// Inlets			LPAssistOut1						// Outlets		}	};#endif