/*	File:		chff~.r	Contains:	Resources for Max/MSP external object chff~.	Written by:	Peter Castine	Copyright:	 © 2000-2002 Peter Castine. All rights reserved.	Change History (most recent first):         <2>     14Ð1Ð04    pc      Set up assistance strings         <1>     10Ð9Ð03    pc      Initial check-in.*/	//	// Configuration values for this object	//	// These must be unique within Litter Package. The Names must match constant values used	// in the C/C++ source code (we try never to access resources by ID).#define LPobjID			17566#define LPobjName		"lp.chff~"	// -----------------------------------------	// 'vers' stuff we need to maintain manually//#define LPobjStarter		1							// Comment out for Pro Bundles#define LPobjMajorRev		1							// 1-99	(decimal)#define LPobjMinorRev		1							// 0-9	(decimal)#define LPobjBugFix			0							// 0-9	(deicmal)#define LPobjStage			developStage				// Standard Apple stage#define LPobjStageBuild		6							// 1-255 (0 for Golden Master only)#define LPobjRegion			0							// US#define LPobjVersStr		"1.1d6"#define LPobjCRYears		2003-08#define	LPobjLitterCategory	"Litter Signals"			// Litter Category#define LPobjMax3Category	"MSP"						// Category for Max 2.2 - 3.6x#define LPobjMax4Category	"MSP Synthesis"				// Category starting at Max 4	// Description string (for Windows Properties box, taken from documentation)#define LPobjDescription	"Pulse train with randomized amplitudes"	// The following sets up the 'mAxL' and 'vers' resources	// It relies on the values above for resource IDs and names, as	// well as concrete values for the 'vers'(1) resource.#include "Litter Globals.r"	//	// -----------------------------------------	//	// Other resource definitions 	//	#ifdef RC_INVOKED		#define LPlessEq		"<="	#else		#define LPlessEq		"²"	#endif		// Assistance strings#define LPAssistIn1			"Float/Signal (pulse interval in ms)"#define LPAssistIn2			"Float/Signal (pulse width, 0 < x < 1)"#define LPAssistIn3			"Float/Signal (phase offset, 0 " LPlessEq " x < 1)"#define LPAssistIn4			"Float/Signal (bang threshhold, 0 " LPlessEq " x < 1)"#define LPAssistOut1		"Signal (Pulse train with random amp)"#define LPAssistOut2		"Bang (when amp > threshhold)"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistIn1		lpStrIndexLastStandard + 2,		LPAssistIn2		lpStrIndexLastStandard + 3,		LPAssistIn3		lpStrIndexLastStandard + 4,		LPAssistIn4		lpStrIndexLastStandard + 5,		LPAssistOut1		lpStrIndexLastStandard + 6,		LPAssistOut2		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray */			LPStdStrings,									// Standard Litter Strings						// Assist strings			LPAssistIn1, LPAssistIn2,						// Inlets			LPAssistIn3, LPAssistIn4,			LPAssistOut1, LPAssistOut2						// Outlets		}	};#endif