kd Button and Valuator counts"#define LPAssistOut2		"trackd Button value"#define LPAssistOut3		"trackd Valuator value"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistIn1		lpStrIndexLastStandard + 2,		LPAssistOut1		lpStrIndexLastStandard + 3,		LPAssistOut2		lpStrIndexLastStandard + 4,		LPAssistOut3		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray */			LPStdStrings,					// Standard Litter Strings						// Assist strings			LPAssistIn1,			LPAssistOut1, LPAssistOut2, LPAssistOut3		}	};#endif