mAxL' and 'vers' resources	// It relies on the values above for resource IDs and names, as	// well as concrete values for the 'vers'(1) resource.#include "Litter Globals.r"	//	// -----------------------------------------	//	// Other resource definitions 	//	// Assistance strings#define LPAssistIn1			"Signal (Real component)"#define LPAssistIn2			"Signal (Imaginary component)"#define LPAssistOut1		"Signal (Amplitude)"#define LPAssistOut2		"Signal (Phase)"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistIn1		lpStrIndexLastStandard + 2,		LPAssistIn2		lpStrIndexLastStandard + 3,		LPAssistOut1		lpStrIndexLastStandard + 4,		LPAssistOut2		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray: 4 elements */			LPStdStrings,					// Standard Litter Strings						// Assist strings			LPAssistIn1, LPAssistIn2,				// Inlets			LPAssistOut1, LPAssistOut2				// Outlets		}	};#endif