/*	File:		-.r	Contains:	Resources for Max/MSP external object lp.-.	Written by:	Peter Castine	Copyright:	 © 2003 Peter Castine. All rights reserved.	Change History (most recent first):         <9>      9Ð1Ð06    pc      Nudge version for Windows bug fix. Update to use                                    LitterAssistResF() utility function.         <8>    14Ð10Ð05    pc      Forgot a comma in the Mac  STR# resource. Fixed. Update vers.         <7>    13Ð12Ð04    pc      Update to use proxy_getinlet(). Also update function names to                                    match object name for the benefit of crash logs.         <6>     21Ð1Ð04    pc      Go to final status.         <5>      8Ð1Ð04    pc      Modify for Rez/RC compatibility.         <4>    6Ð7Ð2003    pc      Bump vers to final (really final).         <3>    9Ð4Ð2003    pc      Update LPobjName.         <2>    8Ð3Ð2003    pc      Oops. Forgot to set c'right date. And we're beta already.         <1>    8Ð3Ð2003    pc      Initial implementation & check-in*/	//	// Configuration values for this object	//	// These must be unique within Litter Package. The Names must match constant values used	// in the C/C++ source code (we try never to access resources by ID).#define LPobjID			17561#define LPobjName		"lp.delta"	// -----------------------------------------	// 'vers' stuff we need to maintain manually//#define LPobjStarter		1								// Comment out for Pro Bundles#define LPobjMajorRev		1#define LPobjMinorRev		2#define LPobjBugFix			0#define LPobjStage			finalStage#define LPobjStageBuild		1#define LPobjRegion			0								// US#define LPobjVersStr		"1.2fc1"#define LPobjCRYears		"2003-08"#define	LPobjLitterCategory	"Litter Utilities"#define LPobjMax3Category	"Arith/Logic/Bitwise"		// Category for Max 2.2 - 3.6x#define LPobjMax4Category	"Math"						// Category starting at Max 4	// Description string (for Windows Properties box, taken from documentation)#define LPobjDescription	"Calculate differences between numbers"	// -----------------------------------------	// The following sets up the 'mAxL', 'vers', and 'Vers' resources	// It relies on the values above for resource IDs and names, as	// well as concrete values for the 'vers'(1) resource.#include "Litter Globals.r"	//	// -----------------------------------------	//	// Other resource definitions 	//		// Assistance strings#define LPAssistInAny		"%s (Triggers subtraction)"#define LPAssistOut1		"%s (left - right)"#define LPAssistOut2		"%s (right - left)"#define LPAssistOut3		"%s (absolute difference)"#define LPAssistFrag1		"int"#define LPAssistFrag2		"float"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistInAny		lpStrIndexLastStandard + 2,		LPAssistOut1		lpStrIndexLastStandard + 3,		LPAssistOut2		lpStrIndexLastStandard + 4,		LPAssistOut3		lpStrIndexLastStandard + 5,		LPAssistFrag1		lpStrIndexLastStandard + 6,		LPAssistFrag2		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray */			LPStdStrings,					// Standard Litter Strings						// Assist strings			LPAssistInAny,								// Inlets			LPAssistOut1, LPAssistOut2, LPAssistOut3,	// Outlets			LPAssistFrag1, LPAssistFrag2				// Fragments		}	};#endif