/*	File:		/.r	Contains:	Resources for Max/MSP external object lp./.	Written by:	Peter Castine	Copyright:	 © 2003 Peter Castine. All rights reserved.	Change History (most recent first):         <9>      9Ð1Ð06    pc      Nudge version for Windows bug fix. Update to use                                    LitterAssistResF() utility function.         <8>    14Ð10Ð05    pc      Forgot a comma in the Mac  STR# resource. Fixed. Update vers.         <7>    13Ð12Ð04    pc      Update to use proxy_getinlet().         <6>    13Ð12Ð04    pc      Add 3rd outlet for modulo (as opposed to remainder).         <5>     21Ð1Ð04    pc      Go to final status.         <4>      8Ð1Ð04    pc      Modify for Rez/RC compatibility.         <3>    6Ð7Ð2003    pc      Bump vers to final (really final).         <2>    9Ð4Ð2003    pc      Update LPobjName.         <1>   11Ð3Ð2003    pc      First implementation, initial check-in*/	//	// Configuration values for this object	//	// These must be unique within Litter Package. The Names must match constant values used	// in the C/C++ source code (we try never to access resources by ID).#define LPobjID			17563#define LPobjName		"lp.logos"	// -----------------------------------------	// 'vers' stuff we need to maintain manually//#define LPobjStarter		1							// Comment out for Pro Bundles#define LPobjMajorRev		2#define LPobjMinorRev		1#define LPobjBugFix			0#define LPobjStage			finalStage#define LPobjStageBuild		1#define LPobjRegion			0							// US#define LPobjVersStr		"2.1fc1"#define LPobjCRYears		"2003-08"#define	LPobjLitterCategory	"Litter Utilities"#define LPobjMax3Category	"Arith/Logic/Bitwise"		// Category for Max 2.2 - 3.6x#define LPobjMax4Category	"Math"						// Category starting at Max 4	// Description string (for Windows Properties box, taken from documentation)#define LPobjDescription	"Quotient, remainder, and modulo"	// The following sets up the 'mAxL', 'vers', and 'Vers' resources	// It relies on the values above for resource IDs and names, as	// well as concrete values for the 'vers'(1) resource.#include "Litter Globals.r"	//	// -----------------------------------------	//	// Other resource definitions 	//		// Assistance strings#define LPAssistNum			"%s (Dividend, triggers division)"#define LPAssistDenom		"%s (Divisor, triggers division)"#define LPAssistOut1		"%s (Quotient)"#define LPAssistOut2		"%s (Remainder)"#define LPAssistOut3		"%s (Modulo)"#define LPAssistFrag1		"int"#define LPAssistFrag2		"float"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistNum		lpStrIndexLastStandard + 2,		LPAssistDenom		lpStrIndexLastStandard + 3,		LPAssistOut1		lpStrIndexLastStandard + 4,		LPAssistOut2		lpStrIndexLastStandard + 5,		LPAssistOut3		lpStrIndexLastStandard + 6,		LPAssistFrag1		lpStrIndexLastStandard + 7,		LPAssistFrag2		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray */			LPStdStrings,					// Standard Litter Strings						// Assist strings			LPAssistNum, LPAssistDenom,					// Inlets			LPAssistOut1, LPAssistOut2, LPAssistOut3,	// Outlets			LPAssistFrag1, LPAssistFrag2				// Fragments		}	};#endif