ources	// It relies on the values above for resource IDs and names, as	// well as concrete values for the 'vers'(1) resource.#include "Litter Globals.r"	//	// -----------------------------------------	// Assistance strings#define LPAssistInAny		"%s (Triggers product of all inlets)"#define LPAssistOut1		"%s (Product)"#define LPAssistFrag1		"int"#define LPAssistFrag2		"float"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistInAny		lpStrIndexLastStandard + 2,		LPAssistOut1		lpStrIndexLastStandard + 3,		LPAssistFrag1		lpStrIndexLastStandard + 4,		LPAssistFrag2		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray */			LPStdStrings,					// Standard Litter Strings						// Assist strings			LPAssistInAny,					// Inlets			LPAssistOut1,					// Outlets			LPAssistFrag1, LPAssistFrag2	// Fragments		}	};#endif