rag1		"scale and offset"#define LPAssistFrag2		"map"#define LPAssistFrag3		"Scale factor"#define LPAssistFrag4		"Offset"#define LPAssistFrag5		"Input range lower bounds"#define LPAssistFrag6		"Input range upper bounds"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistIn1		lpStrIndexLastStandard + 2,		LPAssistIn2		lpStrIndexLastStandard + 3,		LPAssistIn3		lpStrIndexLastStandard + 4,		LPAssistIn4		lpStrIndexLastStandard + 5,		LPAssistIn5		lpStrIndexLastStandard + 6,		LPAssistIn6		lpStrIndexLastStandard + 7,		LPAssistOut1		lpStrIndexLastStandard + 8,		LPAssistOut2a		lpStrIndexLastStandard + 9,		LPAssistOut2b		lpStrIndexLastStandard + 10,	LPAssistOut2c		lpStrIndexLastStandard + 11,	LPAssistFrag1		lpStrIndexLastStandard + 12,	LPAssistFrag2		lpStrIndexLastStandard + 13,	LPAssistFrag3		lpStrIndexLastStandard + 14,	LPAssistFrag4		lpStrIndexLastStandard + 15,	LPAssistFrag5		lpStrIndexLastStandard + 16,	LPAssistFrag6		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray */			LPStdStrings,									// Standard Litter Strings						// Assist strings			LPAssistIn1, LPAssistIn2, LPAssistIn3,			// Inlets			LPAssistIn4, LPAssistIn5, LPAssistIn6,			LPAssistOut1, LPAssistOut2a,					// Outlets			LPAssistOut2b,	LPAssistOut2c,			LPAssistFrag1, LPAssistFrag2, LPAssistFrag3,	// Fragments			LPAssistFrag4, LPAssistFrag5, LPAssistFrag6		}	};#endif