/*	File:		+.r	Contains:	Resources for Max/MSP external object lp.+.	Written by:	Peter Castine	Copyright:	 © _####_ Peter Castine. All rights reserved.	Change History (most recent first):        <10>      9Ð1Ð06    pc      Nudge version for Windows bug fix. Update to use                                    LitterAssistResF() utility function.         <9>    13Ð12Ð04    pc      Update to use proxy_getinlet()         <8>     21Ð1Ð04    pc      Go to final status.         <7>      8Ð1Ð04    pc      Modify for Rez/RC compatibility.         <6>      8Ð1Ð04    pc      Modify for Rez/RC compatibility         <5>    8Ð7Ð2003    pc      Bump revision: fixed problem with list input.         <4>    6Ð7Ð2003    pc      Bump vers to final (really final).         <3>    9Ð4Ð2003    pc      Update LPobjName.         <2>    8Ð3Ð2003    pc      Add STR# resource for Max "finder" categories         <1>    7Ð3Ð2003    pc      First implementation.*/	//	// Configuration values for this object	//	// These must be unique within Litter Package. The Names must match constant values used	// in the C/C++ source code (we try never to access resources by ID).#define LPobjID			17560#define LPobjName		"lp.sigma"	// -----------------------------------------	// 'vers'  and String stuff we need to maintain manually#define LPobjStarter		1								// Comment out for Pro Bundles#define LPobjMajorRev		1#define LPobjMinorRev		2#define LPobjBugFix			0#define LPobjStage			finalStage#define LPobjStageBuild		1#define LPobjRegion			0							// US#define LPobjVersStr		"1.2fc1"#define LPobjCRYears		"2003-08"#define	LPobjLitterCategory	"Litter Utilities"#define LPobjMax3Category	"Arith/Logic/Bitwise"		// Category for Max 2.2 - 3.6x#define LPobjMax4Category	"Math"						// Category starting at Max 4	// Description string (for Windows Properties box, taken from documentation)#define LPobjDescription	"Add numbers"	// The following sets up the 'mAxL', 'vers', and 'Vers' resources	// It relies on the values above for resource IDs and names, as	// well as concrete values for the 'vers'(1) resource.#include "Litter Globals.r"	//	// -----------------------------------------	//	// Other resource definitions 	//		// Assistance strings#define LPAssistInAny		"%s (Triggers sum of all inlets)"#define LPAssistOut1		"%s (Sum)"#define LPAssistFrag1		"int"#define LPAssistFrag2		"float"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistInAny		lpStrIndexLastStandard + 2,		LPAssistOut1		lpStrIndexLastStandard + 3,		LPAssistFrag1		lpStrIndexLastStandard + 4,		LPAssistFrag2		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray */			LPStdStrings,					// Standard Litter Strings						// Assist strings			LPAssistInAny,					// Inlets			LPAssistOut1,					// Outlets			LPAssistFrag1, LPAssistFrag2	// Fragments		}	};#endif