/*	File:		stats.r	Contains:	Resources for Max/MSP external object stacey.	Written by:	Peter Castine	Copyright:	 © 2000-2002 Peter Castine. All rights reserved.	Change History (most recent first):         <8>     21Ð1Ð04    pc      Go to final status.         <7>      8Ð1Ð04    pc      Modify for Rez/RC compatibility.         <6>      2Ð1Ð04    pc      Update for Windows compatibility         <5>    6Ð7Ð2003    pc      Add STR# resource for Object List categories         <4>    7Ð3Ð2003    pc      Bump version to final candidate.         <3>  30Ð12Ð2002    pc      Use 'STR#' resource instead of faux 'Vers' resource for storing                                    version information used at run-time. Also let version info                                    indicate Classic/Carbon-savviness. Bump minor revision numbers.          <2>  29Ð11Ð2002    pc      Tidy up initial check in.         <1>  29Ð11Ð2002    pc      Initial check in.*/	//	// Configuration values for this object	//	// These must be unique within Litter Package. The Names must match constant values used	// in the C/C++ source code (we try never to access resources by ID).#define LPobjID			17497#define LPobjName		"lp.stacey"	// -----------------------------------------	// 'vers' stuff we need to maintain manually#define LPobjStarter		1								// Comment out for Pro Bundles#define LPobjMajorRev		2#define LPobjMinorRev		1#define LPobjBugFix			1#define LPobjStage			finalStage#define LPobjStageBuild		0#define LPobjRegion			0								// US#define LPobjVersStr		"2.1.1"#define LPobjCRYears		"2003-09"#define	LPobjLitterCategory	"Litter Utilities"#define LPobjMax3Category	"Arith/Logic/Bitwise"		// Category for Max 2.2 - 3.6x#define LPobjMax4Category	"Math"						// Category starting at Max 4	// Description string (for Windows Properties box, taken from documentation)#define LPobjDescription	"Collect statistics"	// The following sets up the 'mAxL', 'vers', and 'Vers' resources	// It relies on the values above for resource IDs and names, as	// well as concrete values for the 'vers'(1) resource.#include "Litter Globals.r"	//	// -----------------------------------------	//	// Other resource definitions 	//	// Assistance strings#define LPAssistIn1			"Float or Int (Number is added to statistics)"#define LPAssistOut1		"Int (Count)"#define LPAssistOut2		"Float (Minimum)"#define LPAssistOut3		"Float (Maximum)"#define LPAssistOut4		"Float (Mean)"#define LPAssistOut5		"Float (Standard Deviation)"#define LPAssistOut6		"Float (Skew)"#define LPAssistOut7		"Float (Kurtosis)"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistIn1		lpStrIndexLastStandard + 2,		LPAssistOut1		lpStrIndexLastStandard + 3,		LPAssistOut2		lpStrIndexLastStandard + 4,		LPAssistOut3		lpStrIndexLastStandard + 5,		LPAssistOut4		lpStrIndexLastStandard + 6,		LPAssistOut5		lpStrIndexLastStandard + 7,		LPAssistOut6		lpStrIndexLastStandard + 8,		LPAssistOut7		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray */			LPStdStrings,					// Standard Litter Strings						// Assist strings			LPAssistIn1,			LPAssistOut1,			LPAssistOut2,			LPAssistOut3,			LPAssistOut4,			LPAssistOut5,			LPAssistOut6,			LPAssistOut7		}	};#endif